mapper.insert:
INSERT INTO canvases (
    user_id, title, description, is_public, thumbnail_url,
    nodes, edges, settings
) VALUES (
             #{userId}, #{title}, #{description}, #{isPublic}, #{thumbnailUrl},
             #{nodes,typeHandler=com.conwise.handler.JsonTypeHandler},
             #{edges,typeHandler=com.conwise.handler.JsonTypeHandler},
             #{settings,typeHandler=com.conwise.handler.JsonTypeHandler}
         )

mapper.delete:
UPDATE canvases
SET nodes=(
    SELECT jsonb_agg(elem)
    FROM jsonb_array_elements(nodes) AS elem
    WHERE elem->>'id' !=#{nodeId}
    )
WHERE id=#{canvasId}

UPDATE canvases
SET edges=(
    SELECT jsonb_agg(elem)
    FROM jsonb_array_elements(edges) AS elem
    WHERE elem->>'id' !=#{edgeId}
)
WHERE id=#{canvasId}
